$(document).ready(function(){$("a").on("click",function(event){if(this.hash&&window.location.pathname=="/"){event.preventDefault();var hash=this.hash;$("html, body").animate({scrollTop:$(hash).offset().top},800,function(){window.location.hash=hash})}})});$(document).ready(function(){const f=()=>{if($(window).scrollTop()>=maxOffset){$("nav.navbar").addClass("navbar-shrink")}else{$("nav.navbar").removeClass("navbar-shrink")}};var maxOffset=1;$(window).scroll(function(){f()});f()});$('body').scrollspy({target:'.navbar'})
$('ul.nav li a').click(function(){$("ul.nav").addClass("hidden")});$(document).ready(function(){$("button.navbar-toggle").click(function(){$("ul.nav").toggleClass("hidden")})})
